<span class="line">  server<span class="token punctuation">.</span><span class="token function">addMembership</span><span class="token punctuation">(</span><span class="token string">&#39;230.185.192.108&#39;</span><span class="token punctuation">)</span><span class="token punctuation">;</span></span>
