<span class="line"><span class="token function">logger</span><span class="token punctuation">(</span><span class="token class-name">Calculator</span><span class="token punctuation">.</span>prototype<span class="token punctuation">,</span><span class="token string">&#39;add&#39;</span><span class="token punctuation">,</span>oldDescriptor<span class="token punctuation">)</span><span class="token punctuation">;</span></span>
