<span class="line">  <span class="token keyword">const</span> dependencies <span class="token operator">=</span> Object<span class="token punctuation">.</span><span class="token function">keys</span><span class="token punctuation">(</span>statement<span class="token punctuation">.</span>_dependsOn<span class="token punctuation">)</span><span class="token punctuation">;</span><span class="token comment">//外部依赖 [name]</span></span>
