<span class="line">    <span class="token keyword">return</span> module<span class="token punctuation">.</span><span class="token function">define</span><span class="token punctuation">(</span>exportData<span class="token punctuation">.</span>localName<span class="token punctuation">)</span><span class="token punctuation">;</span></span>
